#% matplotlib inlineimport pandas as pdimport matplotlib.pyplot as pltimport pathlibfrom pathlib import Pathwork_path = pathlib.Path.cwd()data_path = Path(work_path, 'datasets', 'bikes2.csv')plt.style.use('ggplot')plt.rcParams['figure.figsize'] = (15, 5)data = pd.read_csv(data_path, sep=';', encoding='latin1', parse_dates=['Date'], dayfirst=True, index_col='Date')data.plot(figsize=(15, 10))plt.show()