import pandas as pdimport matplotlib.pyplot as pltimport pathlibfrom pathlib import Pathwork_path = pathlib.Path.cwd()data_path = Path(work_path, 'datasets', '311-service-requests.csv')plt.style.use('ggplot')plt.rcParams['figure.figsize'] = (10, 5)complaints = pd.read_csv(data_path)complaint_counts = complaints['Complaint Type'].value_counts()print(complaint_counts[:10])complaint_counts[:10].plot(kind='bar')plt.show()