import pandas as pdimport matplotlib.pyplot as pltimport pathlibfrom pathlib import Pathwork_path = pathlib.Path.cwd()data_path = Path(work_path, 'datasets', 'bikes2.csv')plt.style.use('ggplot')plt.rcParams['figure.figsize'] = (10, 5)bikes = pd.read_csv(data_path, sep=';', encoding='latin1', parse_dates=['Date'], dayfirst=True, index_col='Date')berri_bikes = bikes[['Berri 1']].copy()berri_bikes.loc[:,'weekday'] = berri_bikes.index.weekdayweekday_counts = berri_bikes.groupby('weekday').aggregate(sum)weekday_counts.index = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday']weekday_counts.plot(kind='bar')plt.show()