import pandas as pdimport matplotlib.pyplot as pltimport numpy as npimport pathlibfrom pathlib import Pathpd.options.display.max_rows = 7plt.style.use('ggplot')plt.rcParams['figure.figsize'] = (15, 3)plt.rcParams['font.family'] = 'sans-serif'work_path = pathlib.Path.cwd()data_path = Path(work_path, 'datasets', 'weather.csv')weather_2012 = pd.read_csv(data_path, parse_dates=True, index_col=['Date/Time (LST)'])#print(weather_2012[:5])weather_description = weather_2012['Weather']is_snowing = weather_description.str.contains('Snow')print(is_snowing[:5])temperature = weather_2012['Temp (Â°C)'].resample('M').median()is_snowing = weather_2012['Weather'].str.contains('Snow')snowiness = is_snowing.astype(int).resample('M').mean()# Name the columnstemperature.name = "Temperature"snowiness.name = "Snowiness"stats = pd.concat([temperature, snowiness], axis=1)print(stats)plt.show()