import pandas as pdimport matplotlib.pyplot as pltimport numpy as npimport pathlibfrom pathlib import Pathwork_path = pathlib.Path.cwd()data_path = Path(work_path, 'datasets', '311-service-requests.csv')pd.options.display.max_rows = 7plt.style.use('ggplot')plt.rcParams['figure.figsize'] = (15, 3)plt.rcParams['font.family'] = 'sans-serif'requests = pd.read_csv(data_path)rows_with_dashes = requests['Incident Zip'].str.contains('-').fillna(False)long_zip_codes = requests['Incident Zip'].str.len() > 5requests['Incident Zip'] = requests['Incident Zip'].str.slice(0, 5)zero_zips = requests['Incident Zip'] == '00000'requests.loc[zero_zips, 'Incident Zip'] = np.nanunique_zips = requests['Incident Zip'].unique()zips = requests['Incident Zip']is_close = zips.str.startswith('0') | zips.str.startswith('1')is_far = ~(is_close) & zips.notnull()print(requests['City'].str.upper().value_counts())