import pandas as pdimport matplotlib.pyplot as pltimport numpy as npimport pathlibfrom pathlib import Pathplt.style.use('ggplot')plt.rcParams['figure.figsize'] = (15, 5)work_path = pathlib.Path.cwd()data_path = Path(work_path, 'datasets', 'popularity-contest.csv')popcon = pd.read_csv(data_path, sep=' ')[:-1]popcon.columns = ['atime', 'ctime', 'package-name', 'mru-program', 'tag']popcon['atime'] = popcon['atime'].astype(int)popcon['ctime'] = popcon['ctime'].astype(int)popcon['atime'] = pd.to_datetime(popcon['atime'], unit='s')popcon['ctime'] = pd.to_datetime(popcon['ctime'], unit='s')popcon = popcon[popcon['atime'] > '1970-01-01']nonlibraries = popcon[~popcon['package-name'].str.contains('lib')]print(nonlibraries.sort_values('ctime', ascending=False)[:10])